const UnicodeSymbols: React.FC = () => {
  const copyToClipboard = async (text: string) => {
    await Clipboard.setStringAsync(text);
    ToastAndroid.show('Copied to clipboard!', ToastAndroid.SHORT);
  };

  return (
    <ScrollView className="flex-1 bg-[#0f0f0f] px-4 py-6">
      <View className="space-y-6">
        {/* Popular Symbols Section */}
        <View>
          <Text className="text-white text-xl font-medium text-center mb-4">
            Popular
          </Text>
          <View className="bg-[#141517] rounded-lg p-4">
            <Text className="text-gray-400 text-lg flex-wrap">
              {popularSymbols.split('').map((symbol, index) => (
                <TouchableOpacity
                  key={index}
                  onPress={() => copyToClipboard(symbol)}
                  className="inline-block mx-1"
                >
                  <Text className="text-lg">{symbol}</Text>
                </TouchableOpacity>
              ))}
            </Text>
          </View>
        </View>

        {/* All Symbols Section */}
        <View>
          <Text className="text-white text-xl font-medium text-center mb-4">
            All
          </Text>
          <View className="bg-[#141517] rounded-lg p-4">
            <Text className="text-gray-400 text-lg flex-wrap">
              {allSymbols.split('').map((symbol, index) => (
                <TouchableOpacity
                  key={index}
                  onPress={() => copyToClipboard(symbol)}
                  className="inline-block mx-1"
                >
                  <Text className="text-lg">{symbol}</Text>
                </TouchableOpacity>
              ))}
            </Text>
          </View>
        </View>
      </View>
    </ScrollView>
  );
};

export default UnicodeSymbols; 